import base64

def encrypto_cookies(cookies):
    decoded_bytes = base64.b64decode(cookies)
    return decoded_bytes.decode('utf-8')



decoded_cookies = encrypto_cookies('')


print(decoded_cookies)


import json

# Припускаючи, що decoded_cookies - це JSON рядок
cookies_dict = json.loads(decoded_cookies)
print(cookies_dict)


for cookie in cookies_dict:
    print(cookie["name"], cookie.get("sameSite"))

